		$ret = BasePeer::doUpdate($selectCriteria, $criteria, $con);

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseAnalisisPeer:doUpdate:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseAnalisisPeer', $values, $con, $ret);
