	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{
    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseFuncionarioPeer:doInsert:pre') as $sf_hook)
    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseFuncionarioPeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
			$con = Propel::getConnection(FuncionarioPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from Funcionario object
		}

		if ($criteria->containsKey(FuncionarioPeer::CODIGOFUNCIONARIO) && $criteria->keyContainsValue(FuncionarioPeer::CODIGOFUNCIONARIO) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.FuncionarioPeer::CODIGOFUNCIONARIO.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseFuncionarioPeer:doInsert:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseFuncionarioPeer', $values, $con, $pk);
    }

		return $pk;
	}

