			$comparison = $criteria->getComparison(FuncionarioPeer::CODIGOFUNCIONARIO);
			$selectCriteria->add(FuncionarioPeer::CODIGOFUNCIONARIO, $criteria->remove(FuncionarioPeer::CODIGOFUNCIONARIO), $comparison);

		} else { // $values is Funcionario object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
