	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(TarefaPeer::DATABASE_NAME);

		if ($this->isColumnModified(TarefaPeer::CODIGOTAREFA)) $criteria->add(TarefaPeer::CODIGOTAREFA, $this->codigotarefa);
		if ($this->isColumnModified(TarefaPeer::TAREFA_PARENT)) $criteria->add(TarefaPeer::TAREFA_PARENT, $this->tarefa_parent);
		if ($this->isColumnModified(TarefaPeer::RESPONSAVEL)) $criteria->add(TarefaPeer::RESPONSAVEL, $this->responsavel);
		if ($this->isColumnModified(TarefaPeer::CODIGOPROJETO)) $criteria->add(TarefaPeer::CODIGOPROJETO, $this->codigoprojeto);
		if ($this->isColumnModified(TarefaPeer::VISUALIZACAO)) $criteria->add(TarefaPeer::VISUALIZACAO, $this->visualizacao);
		if ($this->isColumnModified(TarefaPeer::DESCRICAO)) $criteria->add(TarefaPeer::DESCRICAO, $this->descricao);
		if ($this->isColumnModified(TarefaPeer::TIPO_TAREFA)) $criteria->add(TarefaPeer::TIPO_TAREFA, $this->tipo_tarefa);
		if ($this->isColumnModified(TarefaPeer::DATAIRTAREFA)) $criteria->add(TarefaPeer::DATAIRTAREFA, $this->datairtarefa);
		if ($this->isColumnModified(TarefaPeer::DATAFRTAREFA)) $criteria->add(TarefaPeer::DATAFRTAREFA, $this->datafrtarefa);
		if ($this->isColumnModified(TarefaPeer::STATUS)) $criteria->add(TarefaPeer::STATUS, $this->status);
		if ($this->isColumnModified(TarefaPeer::DESPESAPREVISTA)) $criteria->add(TarefaPeer::DESPESAPREVISTA, $this->despesaprevista);
		if ($this->isColumnModified(TarefaPeer::HORASPREVISTAS)) $criteria->add(TarefaPeer::HORASPREVISTAS, $this->horasprevistas);
		if ($this->isColumnModified(TarefaPeer::OBSERVACOES)) $criteria->add(TarefaPeer::OBSERVACOES, $this->observacoes);
		if ($this->isColumnModified(TarefaPeer::INFORMACOES)) $criteria->add(TarefaPeer::INFORMACOES, $this->informacoes);
		if ($this->isColumnModified(TarefaPeer::ID_PRIORIDADE)) $criteria->add(TarefaPeer::ID_PRIORIDADE, $this->id_prioridade);

		return $criteria;
	}

