	 * array. If so the setByName() method is called for that column.
	 *
	 * You can specify the key type of the array by additionally passing one
	 * of the class type constants BasePeer::TYPE_PHPNAME, BasePeer::TYPE_STUDLYPHPNAME,
	 * BasePeer::TYPE_COLNAME, BasePeer::TYPE_FIELDNAME, BasePeer::TYPE_NUM.
	 * The default key type is the column's phpname (e.g. 'AuthorId')
	 *
	 * @param      array  $arr     An array to populate the object from.
	 * @param      string $keyType The type of keys the array uses.
	 * @return     void
	 */
	public function fromArray($arr, $keyType = BasePeer::TYPE_PHPNAME)
	{
		$keys = TempotarefaPeer::getFieldNames($keyType);

		if (array_key_exists($keys[0], $arr)) $this->setCodigoregistro($arr[$keys[0]]);
		if (array_key_exists($keys[1], $arr)) $this->setCodigotarefa($arr[$keys[1]]);
		if (array_key_exists($keys[2], $arr)) $this->setCodigofuncionario($arr[$keys[2]]);
		if (array_key_exists($keys[3], $arr)) $this->setDataprevista($arr[$keys[3]]);
		if (array_key_exists($keys[4], $arr)) $this->setDatareal($arr[$keys[4]]);
		if (array_key_exists($keys[5], $arr)) $this->setTempoprevisto($arr[$keys[5]]);
		if (array_key_exists($keys[6], $arr)) $this->setTempogasto($arr[$keys[6]]);
		if (array_key_exists($keys[7], $arr)) $this->setObservacoes($arr[$keys[7]]);
		if (array_key_exists($keys[8], $arr)) $this->setAutorizado($arr[$keys[8]]);
		if (array_key_exists($keys[9], $arr)) $this->setAutorizadopor($arr[$keys[9]]);
		if (array_key_exists($keys[10], $arr)) $this->setCronograma($arr[$keys[10]]);
		if (array_key_exists($keys[11], $arr)) $this->setLastUpdate($arr[$keys[11]]);
	}

	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(TempotarefaPeer::DATABASE_NAME);

		if ($this->isColumnModified(TempotarefaPeer::CODIGOREGISTRO)) $criteria->add(TempotarefaPeer::CODIGOREGISTRO, $this->codigoregistro);
		if ($this->isColumnModified(TempotarefaPeer::CODIGOTAREFA)) $criteria->add(TempotarefaPeer::CODIGOTAREFA, $this->codigotarefa);
		if ($this->isColumnModified(TempotarefaPeer::CODIGOFUNCIONARIO)) $criteria->add(TempotarefaPeer::CODIGOFUNCIONARIO, $this->codigofuncionario);
		if ($this->isColumnModified(TempotarefaPeer::DATAPREVISTA)) $criteria->add(TempotarefaPeer::DATAPREVISTA, $this->dataprevista);
		if ($this->isColumnModified(TempotarefaPeer::DATAREAL)) $criteria->add(TempotarefaPeer::DATAREAL, $this->datareal);
		if ($this->isColumnModified(TempotarefaPeer::TEMPOPREVISTO)) $criteria->add(TempotarefaPeer::TEMPOPREVISTO, $this->tempoprevisto);
		if ($this->isColumnModified(TempotarefaPeer::TEMPOGASTO)) $criteria->add(TempotarefaPeer::TEMPOGASTO, $this->tempogasto);
		if ($this->isColumnModified(TempotarefaPeer::OBSERVACOES)) $criteria->add(TempotarefaPeer::OBSERVACOES, $this->observacoes);
		if ($this->isColumnModified(TempotarefaPeer::AUTORIZADO)) $criteria->add(TempotarefaPeer::AUTORIZADO, $this->autorizado);
		if ($this->isColumnModified(TempotarefaPeer::AUTORIZADOPOR)) $criteria->add(TempotarefaPeer::AUTORIZADOPOR, $this->autorizadopor);
		if ($this->isColumnModified(TempotarefaPeer::CRONOGRAMA)) $criteria->add(TempotarefaPeer::CRONOGRAMA, $this->cronograma);
		if ($this->isColumnModified(TempotarefaPeer::LAST_UPDATE)) $criteria->add(TempotarefaPeer::LAST_UPDATE, $this->last_update);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
