	public function copyInto($copyObj, $deepCopy = false)
	{

		$copyObj->setCodigotarefa($this->codigotarefa);

		$copyObj->setCodigofuncionario($this->codigofuncionario);

		$copyObj->setDataprevista($this->dataprevista);

