		$copyObj->setAutorizadopor($this->autorizadopor);

		$copyObj->setCronograma($this->cronograma);

		$copyObj->setLastUpdate($this->last_update);


		$copyObj->setNew(true);

		$copyObj->setCodigoregistro(NULL); // this is a auto-increment column, so set to default value

