	}

	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(SfSectionPeer::DATABASE_NAME);

		if ($this->isColumnModified(SfSectionPeer::ID)) $criteria->add(SfSectionPeer::ID, $this->id);
		if ($this->isColumnModified(SfSectionPeer::ID_PROFILE)) $criteria->add(SfSectionPeer::ID_PROFILE, $this->id_profile);
		if ($this->isColumnModified(SfSectionPeer::ID_PARENT)) $criteria->add(SfSectionPeer::ID_PARENT, $this->id_parent);
		if ($this->isColumnModified(SfSectionPeer::POSITION)) $criteria->add(SfSectionPeer::POSITION, $this->position);
		if ($this->isColumnModified(SfSectionPeer::CONTROL)) $criteria->add(SfSectionPeer::CONTROL, $this->control);
		if ($this->isColumnModified(SfSectionPeer::SW_MENU)) $criteria->add(SfSectionPeer::SW_MENU, $this->sw_menu);
		if ($this->isColumnModified(SfSectionPeer::STATUS)) $criteria->add(SfSectionPeer::STATUS, $this->status);
		if ($this->isColumnModified(SfSectionPeer::HOME)) $criteria->add(SfSectionPeer::HOME, $this->home);
		if ($this->isColumnModified(SfSectionPeer::SPECIAL_PAGE)) $criteria->add(SfSectionPeer::SPECIAL_PAGE, $this->special_page);
		if ($this->isColumnModified(SfSectionPeer::SHOW_TEXT)) $criteria->add(SfSectionPeer::SHOW_TEXT, $this->show_text);
		if ($this->isColumnModified(SfSectionPeer::ONLY_COMPLEMENT)) $criteria->add(SfSectionPeer::ONLY_COMPLEMENT, $this->only_complement);
		if ($this->isColumnModified(SfSectionPeer::URL_EXTERNA)) $criteria->add(SfSectionPeer::URL_EXTERNA, $this->url_externa);
		if ($this->isColumnModified(SfSectionPeer::DELETE)) $criteria->add(SfSectionPeer::DELETE, $this->delete);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
