			SfSectionI18nPeer::clearInstancePool();
			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof SfSectionI18n) { // it's a model object
			// invalidate the cache for this single object
			SfSectionI18nPeer::removeInstanceFromPool($values);
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else { // it's a primary key, or an array of pks
			$criteria = new Criteria(self::DATABASE_NAME);
			// primary key is composite; we therefore, expect
			// the primary key passed to be an array of pkey values
			if (count($values) == count($values, COUNT_RECURSIVE)) {
				// array is not multi-dimensional
				$values = array($values);
			}
			foreach ($values as $value) {
				$criterion = $criteria->getNewCriterion(SfSectionI18nPeer::ID, $value[0]);
				$criterion->addAnd($criteria->getNewCriterion(SfSectionI18nPeer::LANGUAGE, $value[1]));
				$criteria->addOr($criterion);
				// we can invalidate the cache for this single PK
				SfSectionI18nPeer::removeInstanceFromPool($value);
			}
		}

		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$affectedRows = 0; // initialize var to track total num of affected rows

		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			
			$affectedRows += BasePeer::doDelete($criteria, $con);
			SfSectionI18nPeer::clearRelatedInstancePool();
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * Validates all modified columns of given SfSectionI18n object.
	 * If parameter $columns is either a single column name or an array of column names
	 * than only those columns are validated.
	 *
	 * NOTICE: This does not apply to primary or foreign keys for now.
	 *
	 * @param      SfSectionI18n $obj The object to validate.
	 * @param      mixed $cols Column name or array of column names.
	 *
	 * @return     mixed TRUE if all columns are valid or the error message of the first invalid column.
	 */
	public static function doValidate(SfSectionI18n $obj, $cols = null)
	{
		$columns = array();

		if ($cols) {
			$dbMap = Propel::getDatabaseMap(SfSectionI18nPeer::DATABASE_NAME);
			$tableMap = $dbMap->getTable(SfSectionI18nPeer::TABLE_NAME);

			if (! is_array($cols)) {
				$cols = array($cols);
			}

			foreach ($cols as $colName) {
				if ($tableMap->containsColumn($colName)) {
					$get = 'get' . $tableMap->getColumn($colName)->getPhpName();
					$columns[$colName] = $obj->$get();
				}
			}
		} else {

		}

		return BasePeer::doValidate(SfSectionI18nPeer::DATABASE_NAME, SfSectionI18nPeer::TABLE_NAME, $columns);
	}

	/**
	 * Retrieve object using using composite pkey values.
	 * @param      int $id
	 * @param      string $language
	 * @param      PropelPDO $con
	 * @return     SfSectionI18n
	 */
	public static function retrieveByPK($id, $language, PropelPDO $con = null) {
		$key = serialize(array((string) $id, (string) $language));
 		if (null !== ($obj = SfSectionI18nPeer::getInstanceFromPool($key))) {
 			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(SfSectionI18nPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}
		$criteria = new Criteria(SfSectionI18nPeer::DATABASE_NAME);
		$criteria->add(SfSectionI18nPeer::ID, $id);
		$criteria->add(SfSectionI18nPeer::LANGUAGE, $language);
		$v = SfSectionI18nPeer::doSelect($criteria, $con);

		return !empty($v) ? $v[0] : null;
	}
